import React from 'react';
import image1 from './pictures/1.png';
import image2 from './pictures/2.jpg';
import '../App.css';

function AboutUs() {
  const images = [
    { src: image1, text: "Raffy Paul Carbo", alignment: "flex-right" },
    { src: image2, text: "Image 2 Text", alignment: "flex-left" },
    { src: "your-image3.jpg", text: "Image 3 Text", alignment: "flex-right" },
    { src: "your-image3.jpg", text: "Image 3 Text", alignment: "flex-left" },
    { src: "your-image3.jpg", text: "Image 3 Text", alignment: "flex-right" },
  ];

  return (
    <div className="container">
      <h1 className="heading">Meet the Team</h1>
      <div className="image-gallery">
        {images.map((image, index) => (
          <div key={index} className={`flex items-center ${image.alignment}`}>
            <div className="image-container">
              <img src={image.src} alt={`Image ${index + 1}`} />
            </div>
            <p className="text-box">{image.text}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AboutUs;
