import React from 'react';
import image1 from './pictures/1.png';
import './App.css'; // Import your CSS file

function AboutUs() {
  const images = [
    { src: image1, text: "Image 1 Text", alignment: "flex-right" },
    { src: "your-image2.jpg", text: "Image 2 Text", alignment: "flex-left" },
    { src: "your-image3.jpg", text: "Image 3 Text", alignment: "flex-right" },
  ];

  return (
    <div className="grid grid-cols-1 gap-4 overflow-y-auto h-screen">
      {images.map((image, index) => (
        <div key={index} className={`flex items-center ${image.alignment}`}>
          <div className="image-container">
            <img src={image.src} alt={`Image ${index + 1}`} />
          </div>
          <p className="text-box">{image.text}</p>
        </div>
      ))}
    </div>
  );
}

export default AboutUs;
