import React from 'react';

const products = [
  { src: 'latte.jpg', name: 'LATTE', price: '₱140.00' },
  { src: 'americano.jpg', name: 'AMERICANO', price: '₱145.00' },
  { src: 'machiatto.jpg', name: 'MACHIATTO', price: '₱120.00' },
  { src: 'flatwhite.jpg', name: 'FLAT-WHITE', price: '₱115.00' },
  { src: 'capuccino.jpg', name: 'CAPUCCINO', price: '₱135.00' },
  { src: 'espresso.jpg', name: 'ESPRESSO', price: '₱145.00' },
];

const Menu = () => {
  return (
    <section className="bg-black py-10">
      <h1 className="text-center text-4xl text-white bg-gray-800 p-4">OUR <span className="text-red-500">MENU</span></h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 p-4">
        {products.map((product, index) => (
          <div key={index} className="bg-white p-4 text-center rounded-lg shadow-lg hover:shadow-xl transition-shadow">
            <img src={`pictures/${product.src}`} alt={product.name} className="h-40 w-full object-cover rounded-full mb-2" />
            <div className="font-bold text-lg">{product.name}</div>
            <div className="text-xl">{product.price}</div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Menu;
