import React from 'react';
import image1 from './pictures/1.png'; // Ensure you import other images if needed

function AboutUs() {
  return (
    <div className="grid grid-cols-1 gap-4 overflow-y-auto h-screen">
      <div className="flex items-center justify-center">
        <div className="w-48 h-48 bg-gray-200 rounded-lg overflow-hidden flex-shrink-0">
          <img src={image1} alt="Image 1" className="w-full h-full object-cover" />
        </div>
        <p className="text-lg ml-4">Image 1 Text</p> {/* Added margin to the left (ml-4) */}
      </div>

      <div className="flex items-center justify-center">
        <div className="w-48 h-48 bg-gray-200 rounded-lg overflow-hidden flex-shrink-0">
          <img src="your-image2.jpg" alt="Image 2" className="w-full h-full object-cover" />
        </div>
        <p className="text-lg ml-4">Image 2 Text</p>
      </div>

      <div className="flex items-center justify-center">
        <div className="w-48 h-48 bg-gray-200 rounded-lg overflow-hidden flex-shrink-0">
          <img src="your-image3.jpg" alt="Image 3" className="w-full h-full object-cover" />
        </div>
        <p className="text-lg ml-4">Image 3 Text</p>
      </div>
      {/* Add more divs as needed */}
    </div>
  );
}

export default AboutUs;
